const pwcli = require('./index.js');

let CliConfig = {
  greet($name) {
    pwcli.write_output('Hello, $name!');
  },

  mood($mood, $Verbose) {
    pwcli.write_output('Today you feel: $mood');
    if ($Verbose) pwcli.write_output('Verbose is enabled!', { italic: true });
  }
};

pwcli.use(CliConfig);
pwcli.set_switches(['Verbose']);
pwcli.parse(process.argv.slice(2));
