// myModule.js

function processText(text) {
    // console.log("Inside processText"); // Removed for cleaner tracer output
    return text.toUpperCase();
}

function calculate(num) {
    // console.log("Inside calculate"); // Removed for cleaner tracer output
    return num * 2;
}

function doSomething(param1, param2) {
    // console.log("Inside doSomething"); // Removed for cleaner tracer output
    const processedText = processText(param1);
    const calculatedValue = calculate(param2);
    return { processedText, calculatedValue };
}

